		Matcher matcher_email = pattern_email.matcher(email);

		Pattern pattern_telefono = Pattern.compile("\\d{4}-\\d{4}");
		Matcher matcher_telefono = pattern_telefono.matcher(telefono);

		Usuario usr = new Usuario();

		try {
			if (!matcher_email.matches()) {
				response.append("email", "fail");
				status = false;
			} else {
				response.append("email", "success");
			}

			if (!matcher_telefono.matches()) {
				response.append("telefono", "fail");
				status = false;
			} else {
				response.append("telefono", "success");
			}

			if (status) {

				usr.setUsuario(usuario);
				usr.setNombre(nombre);
				usr.setApellidos(apellidos);
				usr.setTelefono(telefono);
				usr.setPassword(Utils.encodeToSHA512(password));
				usr.setTelefono(telefono);
				usr.setEmail(email);

				usuarioService.create(usr);

				response.append("general", "success");

			} else {

				response.append("general", "fail");

			}

		} catch (Exception excep) {
			excep.printStackTrace();
		}

		return response.toString();

